import React, { Component } from 'react'
import Swiper from "swiper";
export default class BannerText extends Component {
    componentDidUpdate(){
        new Swiper(".swiper-container",{
            direction:"vertical",
            autoplay:true,
            loop:true,
            centeredSlides: false, // 选中slide居中显示
            initialSlide: 1, // 默认选中项索引
            slidesPerView: 1, // 自动匹配每次显示的slide个数,loop='auto'模式下，还需要设置loopedSlides
            effect: 'coverflow', // 切换效果-3d
            coverflowEffect: {
             rotate: 0,
             stretch: 10,
             depth: 160,
             modifier: 2,
             slideShadows: true
            }
        })
    }
    render() {
        return (
            <div className="swiper-container bannerText-box">
            <div className="swiper-wrapper">
                {
                    this.props.list.map((item,index)=>{
                        return (
                            <div className="swiper-slide" key={index}>
                                <img src={item.text} alt=""/>
                            </div>
                        )
                    })
                }  
            </div>
        </div>
        )
    }
}
